sp_widget.client_script:
function overviewController( codingStandardService ) {

	var c = this;

	c.codingStandardService = codingStandardService;

	//Initialize service as empty
	c.codingStandardService.setStandards( [] );
	c.codingStandardService.setSelectedIndex( '' );

	c.codingStandardService.setStandards( c.data.standards );
	c.standards = c.codingStandardService.data.standards;

	// onLoad: start looping through the list of best practices to pull in the data
	c.standards.forEach(function( elem, i ) {
		c.server.get({
			codingStandardSysID: elem.sys_id,
			index: i
		}).then( updateStandardObj );
	});

	function updateStandardObj( response ) {
		c.codingStandardService.updateOffenders( response.data.index, response.data.standardOffenders );
	}

}

